function get_duration_2(from_1, to_1, from_2 = "", to_2 = "", from_3 = "", to_3 = "")
{
    var duration = [];
    for (let index = 0; index < 47; index++)
    {
        if (from_1 < to_1)
        {
            if (from_1 <= index && index < to_1)
            {
                duration[index] = true;
            }
        }
        if (from_2 < to_2 && from_2 != "" && to_2 != "")
        {
            if (from_2 <= index && index < to_2)
            {
                duration[index] = true;
            }
        }
        if (from_3 < to_3 && from_3 != "" && to_3 != "")
        {
            if (from_3 <= index && index < to_3)
            {
                duration[index] = true;
            }
        }
        if (from_1 > to_1)
        {
            if (from_1 <= index || index < to_1)
            {
                duration[index] = true;
            }
        }
        if (from_2 > to_2 && from_2 != "" && to_2 != "")
        {
            if (from_2 <= index || index < to_2)
            {
                duration[index] = true;
            }
        }
        if (from_3 > to_3 && from_3 != "" && to_3 != "")
        {
            if (from_3 <= index || index < to_3)
            {
                duration[index] = true;
            }
        }
    }

    var result = 0;

    for (let index = 0; index < 47; index++)
    {
        if (duration[index])
        {
            result = result + 1;
        }
    }

    result = converter("dec", "time_2", result);

    return result;
}

function getName(d) {
    const error = new Error();
    const firefoxMatch = (error.stack.split('\n')[0 + d].match(/^.*(?=@)/) || [])[0];
    const chromeMatch = ((((error.stack.split('at ') || [])[1 + d] || '').match(/(^|\.| <| )(.*[^(<])( \()/) || [])[2] || '').split('.').pop();
    const safariMatch = error.stack.split('\n')[0 + d];

    // firefoxMatch ? console.log('firefoxMatch', firefoxMatch) : void 0;
    // chromeMatch ? console.log('chromeMatch', chromeMatch) : void 0;
    // safariMatch ? console.log('safariMatch', safariMatch) : void 0;

    return firefoxMatch || chromeMatch || safariMatch;
}


