function is_void(value) {
    if (value === 0)
        return true;
    if (value === "0")
        return true;
    if (value === "")
        return true;
    if (value === undefined)
        return true;
}

function reverse_bool(bool) {
    if (bool === false)
        return true;
    if (bool === true)
        return false;
    if (bool === 0)
        return 1;
    if (bool === 1)
        return 0;
    if (bool === "0")
        return "1";
    if (bool === "1")
        return "0";
}

