function show_history(flow_on, eq_modal)
{
    eq_modal = parseInt(eq_modal);

    while (eq_modal > 0) {

        var html = $("[data-template='vigipool_" + flow_on + "']").eq(eq_modal).html();

        if (html !== undefined) {
            $("[data-template='vigipool_" + flow_on + "']").eq(eq_modal).click();
            return;
        }

        eq_modal = eq_modal - 1;
    }
}

